Ext.define('MyApp.controller.ImportImportPanelController', {
    extend: 'Ext.app.Controller',

    refs: [
        {
            autoCreate: true,
            ref: 'ImportImportPanel',
            selector: '#ImportImportPanel',
            xtype: 'importimportpanel'
        },
        {
            autoCreate: true,
            ref: 'ImportPanel',
            selector: '#ImportPanel',
            xtype: 'importpanel'
        },
        {
            autoCreate: true,
            ref: 'ImportActionPanel',
            selector: '#ImportActionPanel',
            xtype: 'importactionpanel'
        }
    ],

    onFormEditButtonClick: function(button, e, eOpts) {
        panel = this.getImportImportPanel();

        formPanel = panel.getComponent('FormPanel');
        toolbar = panel.getComponent('FormToolbar');
        formPanel.enable();

        toolbar.getComponent('FormEditButton').disable();
        toolbar.getComponent('FormNewButton').disable();
        toolbar.getComponent('FormCancelButton').enable();
        toolbar.getComponent('FormSaveButton').enable();
        toolbar.getComponent('FormDeleteButton').disable();

    },

    onFormNewButtonClick: function(button, e, eOpts) {
        store = Ext.getStore('ImportImportJsonStore');
        panel = this.getImportImportPanel();

        formPanel = panel.getComponent('FormPanel');
        toolbar = panel.getComponent('FormToolbar');
        if (store !== undefined) {
            //formPanel = button.ownerCt.ownerCt.ownerCt.query('#" . $name . "FormPanel')[0];
            formPanel.enable();
            record = new store.model();
            formPanel.loadRecord(record);
            toolbar.getComponent('FormEditButton').disable();
            toolbar.getComponent('FormNewButton').disable();
            toolbar.getComponent('FormCancelButton').enable();
            toolbar.getComponent('FormSaveButton').enable();
            toolbar.getComponent('FormDeleteButton').disable();
        }

    },

    onFormSaveButtonClick: function(button, e, eOpts) {
        store = Ext.getStore('ImportImportJsonStore');
        panel = this.getImportImportPanel();

        formPanel = panel.getComponent('FormPanel');
        toolbar = panel.getComponent('FormToolbar');

        //grid = button.ownerCt.ownerCt.ownerCt.query('#" . $name . "GridPanel')[0];

        record = formPanel.getForm().getRecord();
        if (record !== undefined && (record.data.id === undefined || record.data.id == 0)) {
            values = formPanel.getForm().getValues();
            record.set(values);
            store.insert(0, record);
            /*grid = button.ownerCt.ownerCt.ownerCt.getComponent('" . $name . 'GridPanel' . "');*/
            //if (grid !== undefined) {
            /*grid.getView().select(0);*/ /* BUG!!! */
            //}
        } else {
            formPanel.getForm().updateRecord();
        }
        formPanel.disable();
        toolbar.getComponent('FormEditButton').enable();
        toolbar.getComponent('FormNewButton').enable();
        toolbar.getComponent('FormCancelButton').disable();
        toolbar.getComponent('FormSaveButton').disable();
        toolbar.getComponent('FormDeleteButton').enable();
    },

    onFormCancelButtonClick: function(button, e, eOpts) {
        panel = this.getImportImportPanel();
        grid = this.getImportPanel().getComponent('ImportImportGridPanel');

        formPanel = panel.getComponent('FormPanel');
        toolbar = panel.getComponent('FormToolbar');
        record = formPanel.getForm().getRecord();								
        if (record !== undefined) {
            formPanel.getForm().reset();
            formPanel.disable();
            toolbar.getComponent('FormEditButton').enable();
            toolbar.getComponent('FormNewButton').enable();
            toolbar.getComponent('FormCancelButton').disable();
            toolbar.getComponent('FormSaveButton').disable();
        }

        if(grid !== undefined && grid.getView().getNodes().length > 0) {
            grid.getView().select(0);
            record = grid.getSelectionModel().getSelection()[0];
            formPanel.getForm().loadRecord(record);
        }
    },

    onFormDeleteButtonClick: function(button, e, eOpts) {
        var store = Ext.getStore('ImportImportJsonStore');
        var panel = this.getImportImportPanel(); 
        var grid = this.getImportPanel().getComponent('GridPanel');

        var that = this;

        Ext.Msg.confirm('Attention!', 'Datensatz lÃ¶schen?', function(btn) {
            if(btn == 'yes') {
                //grid = button.ownerCt.ownerCt.ownerCt.query('#" . $name . "GridPanel')[0];

                formPanel = panel.getComponent('FormPanel');
                record = formPanel.getForm().getRecord();
                if (store !== undefined && record !== undefined) {
                    store.remove(record);
                    formPanel.getForm().reset();
                    if(grid !== undefined && grid.getView().getNodes().length > 0) {
                        grid.getView().select(0);
                    }
                }
            }
        });
    },

    init: function(application) {
        this.control({
            "#FormEditButton": {
                click: this.onFormEditButtonClick
            },
            "#FormNewButton": {
                click: this.onFormNewButtonClick
            },
            "#FormSaveButton": {
                click: this.onFormSaveButtonClick
            },
            "#FormCancelButton": {
                click: this.onFormCancelButtonClick
            },
            "#FormDeleteButton": {
                click: this.onFormDeleteButtonClick
            }
        });
    }

});
