Ext.define('MyApp.controller.RuleController', {
    extend: 'Ext.app.Controller',

    rights: {
        CREATE: 1,
        READ: 2,
        UPDATE: 4,
        DELETE: 8,
        EXECUTE: 16,
        RELEASE: 32
    },

    refs: [
        {
            autoCreate: true,
            ref: 'MainPanel',
            selector: '#MainPanel',
            xtype: 'mainpanel'
        }
    ],

    allow: function(resource, right) {
        var me = this;
        var store = Ext.getStore('ActiveUserResourcesJsonStore');

        if (Ext.isEmpty(resource)) return true;

        var rule = store.findRecord('slug', resource);

        if (Ext.isEmpty(rule)) return false;

        return (right & rule.data.allow & ~rule.data.deny) > 0;

    },

    ruleMainMenuItems: function() {
        var me = this;

        var mainPanel = me.getMainPanel();

        var menuitems = mainPanel.down('#MainToolbar').query('menuitem');

        for(var idx in menuitems) {
            if (!me.allow(menuitems[idx].itemId, me.rights.READ)) {
                menuitems[idx].destroy();
            }
        }

        var buttons = mainPanel.down('#MainToolbar').query('button');

        for(idx in buttons) {
            if (!me.allow(buttons[idx].itemId, me.rights.READ)) {
                buttons[idx].destroy();
            }
        }
    }

});
